class TextEditLogger(QPlainTextEdit):
    def __init__(self, parent=None):
        super().__init__(parent)
        self.buffer = []  # Буфер для накопления сообщений
        self.timer = QTimer(self)
        self.timer.timeout.connect(self.flush_buffer)  # Подключаем таймер к методу сброса буфера
        self.timer.start(100)  # Обновляем каждые 100 мс

    def write(self, text):
        """Добавляет текст в буфер."""
        self.buffer.append(text)

    def flush_buffer(self):
        """Сбрасывает буфер и обновляет текстовое поле."""
        if self.buffer:
            # Объединяем все сообщения из буфера в одну строку
            self.appendPlainText("".join(self.buffer))
            self.buffer.clear()  # Очищаем буфер

    def flush(self):
        """Метод для совместимости с sys.stdout."""
        self.flush_buffer()


